// Exemplo de decomposição de um problema
function processUserData(users) {
    // 1. Filtrar usuários ativos
    const activeUsers = users.filter(user => user.active);

    // 2. Calcular média de idade
    const averageAge = activeUsers.reduce((sum, user) => sum + user.age, 0) / activeUsers.length;

    // 3. Agrupar por cidade
    const usersByCity = activeUsers.reduce((groups, user) => {
        const city = user.city;
        if (!groups[city]) groups[city] = [];
        groups[city].push(user);
        return groups;
    }, {});

    return { activeUsers, averageAge, usersByCity };
}

// Integração com Gemini API
async function sendToGeminiAPI(message) {
    const apiKey = 'SUA_CHAVE_API';
    const url = `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${apiKey}`;

    const response = await fetch(url, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            contents: [{
                parts: [{
                    text: message
                }]
            }]
        })
    });

    const data = await response.json();
    return data.candidates[0].content.parts[0].text;
}
